document.addEventListener("DOMContentLoaded",function(){const a=document.querySelector(".formfeedback__tabs"),c=document.querySelectorAll(".formfeedback__form");a.addEventListener("click",e=>{if(e.target.dataset.btn){var t=e.target.dataset.btn;(e=>{for(let e=0;e<a.children.length;e++)a.children[e].classList.remove("active");e.classList.add("active")})(e.target);for(let e=0;e<c.length;e++)c[e].classList.remove("active"),c[e].dataset.content===t&&c[e].classList.add("active")}})});